package com.damoim.android.ui.main.fragment.image

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import com.damoim.android.R
import com.damoim.android.base.BaseFragment
import com.damoim.android.databinding.FragmentImageBinding
import com.damoim.android.ui.main.fragment.image.adapter.ImageAdapter


class ImageFragment : BaseFragment<FragmentImageBinding>(
    FragmentImageBinding::bind,
R.layout.fragment_image) {
    private val imageAdapter by lazy { ImageAdapter() }
    private val images = arrayListOf("https://upload.wikimedia.org/wikipedia/commons/thumb/7/70/Milan_Cathedral_from_Piazza_del_Duomo.jpg/270px-Milan_Cathedral_from_Piazza_del_Duomo.jpg",
        "data:image/jpeg;base64,",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmdZdSwc_eqJBBqQ5VO66JHQeyup-FJuGy6g&usqp=CAU")

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initView()
    }

    private fun initView() {
        with(binding) {
            imagePager.adapter = imageAdapter
            imageAdapter.setImageList(images)
            imageIndicator.attachTo(imagePager)

            imageImgClose.setOnClickListener {
                findNavController().popBackStack()
            }
        }
    }

}